      if (platform === 'wx') {
          //微信平台 - 进行微信登录
          TGLoginManager.mWXLogin(function(uinfo) {
              window.global.name = uinfo.nickname;
          });
      } else if (platform === 'qq') {
          //QQ平台 - 进行手Q登录
          TGLoginManager.mQQLogin(function(uinfo) {
              window.global.name = uinfo.nickname;
          }); 
      } else {
          //非微信、QQ平台
          //拉起手QAPP打开页面
          TGLoginManager.mOpenQQ();
      }
  });



